import React, {useState, useEffect} from "react";
import ReactDOM from "react-dom";

function Post(){
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [postList, setPostList] = useState([]);

    useEffect(() => {
        const requestOptions = {
            method: 'POST',
            body: JSON.stringify(1), // Convert 1 to a JSON string
            headers: {
                'Content-Type': 'application/json',
            },
        };

        fetch("/Post/getAllPosts/", JSON.stringify(1))
        .then(res => res.json())
        .then(
            (result) => {
                setIsLoaded(true);
                setPostList(result);
            },
            (error) => {
                setIsLoaded(true);
                setError(error);  
                console.log(error);
            }
        )
    }, [])

    if(error){
        return <div> Error !!! </div>
    } else if(!isLoaded){
        return <div> Loading... </div>
    } else {
        return(
            <ul>
                {postList.map(post => (
                    <li>
                        {post.title} {post.text}
                    </li>
                ))}
            </ul>
        );
    }
}

export default Post;
